import fetch from 'node-fetch'

const Card = ({card}) => {
    

return <h1>{card.id} and name : {card.name}</h1>
}


export async function getStaticProps({params}){

    const res = await fetch(`http://localhost:3000/api/card/${params.id}`)

    const card = await res.json()

    return ({props:{card}})
}

export async function getStaticPaths(){

    const res = await fetch(`http://localhost:3000/api/all/discover`)
    const allpaths = await res.json()

    const paths = allpaths.map(data=> ({
        params: {id:toString(data.id)}
    }))
    return {
        paths,
        fallback:false
    }
}


export default Card
