import React from 'react'
import {countries} from '../../datas/countriesData'

import {
  Container,
  Row,
  Col
} from 'reactstrap'


const Card = ({lesdatas}) => {



return (

  <Container style={{padding: 0,maxWidth:'100%' , margin: 0}}>

    <header className="product-header">
    

    <img style={{objectFit:'cover'}} width="100%" height="100%" alt="undefined" src={`/images/${lesdatas.image}`} />
  
<h1>{lesdatas.name}</h1>
    </header>

    <div>
      <h1>
        bonjot
      </h1>
    </div>

    
    

    <style jsx global>{`

   .product-header{
     height:45vh;
     width:100%
   }

   .product-header h1{
     position:absolute;
     top:40%;
     left:30%;
     z-index:1500;
     
     color:white;
     text-align:center;
     text-transform:uppercase;
    font-weight:900;
    font-size:4em;
    letter-spacing: 0.3em;
    
   }

   .product-header::after{
     display:block;
     content:'';
     height:45vh;
     width:100%;
     position:absolute;
     top:0;
     background-color:black;
     opacity:0.5
   }

    `}
    </style>
  </Container>
  

)

}

export async function getStaticProps({params}){

 
    const allpaths =  countries
 
    
   let  restos =[] 
   let leshotels = [];
    


     allpaths.map(pays => pays.restaurants).forEach(tabresto => tabresto.map(resto => restos.push(resto)))

     allpaths.map(pays => pays.hotels).forEach(tabhotel => tabhotel.map(hotel => leshotels.push(hotel)))
 
     const datas = restos.concat(leshotels)

     const lesdatas = datas.find(data => data.name === params.id)



   return {props: {lesdatas}}

}

export async function getStaticPaths(){
    
     const allpaths =  countries
  
     let paths = [] 
    let  restos =[] 
    let leshotels = [];
     


      allpaths.map(pays => pays.restaurants).forEach(tabresto => tabresto.map(resto => restos.push(resto)))

      allpaths.map(pays => pays.hotels).forEach(tabhotel => tabhotel.map(hotel => leshotels.push(hotel)))

      const datas = restos.concat(leshotels)

     paths = datas.map(data => ({
        params:{id: data.name}
    }))

     

     return{paths,fallback:false}
}

export default Card
