import React,{useState} from 'react'

import Image from '../components/Image'
import
{
Container,
Row,
Col,
Form,
FormGroup,
Label,
Input

}from 'reactstrap'


const categories =  [
    {
        id:0,
        name:'Tous',
        titrediv:'Top Hotelerie Top restaurantion'
    },
    {
        id:1,
        name:'Hotelerie',
        titrediv:'Top Hotelerie'
    },
    {
        id:2,
        name:'restaurations',
        titrediv:'Top restaurations'
    }
]

const Discover = () => {

        const [catVal,setCatVal] = useState('tous')

    const handleChange = e => {

        setCatVal(e.target.value)
            
    }

    return(
        <Container className="discover-page">
          
            <header className="discover-header">
                <Image image={{width:'100%',height:'100%',alt:'pagne img',src:'/images/pagne.jpg'}} />
            </header>

            <section className="filtre-section">

               <div className="filter-div">
                <h3>Filtre :</h3>
        <FormGroup className="form-discover">
            <Label for="exampleSelect">Pays</Label>
            <Input type="select" name="select" id="exampleSelect">
            <option>1</option>
            <option>2</option>
            <option>3</option>
            <option>4</option>
            <option>5</option>
            </Input>
      </FormGroup>
 
      <FormGroup className="form-discover">
        <Label for="exampleSelect">Cat√©gorie</Label>
        <Input onChange={(e)=>handleChange(e)} type="select" name="select" id="exampleSelect">
    {categories.map(cat => <option key={cat.id}>{cat.name}</option>)}
        </Input>
      </FormGroup>
 
                  
               </div>
            </section>

            <section className="categories-section">

                {
                    catVal === 'Tous' ? categories.find(cat => cat.name === 'Tous' ).titrediv.split(" ").map(val => <div key={val}><h1>{val}</h1></div>):<h1>non</h1>
                }

            </section>


    <style>{`
    
    .discover-page{
        padding-right:0;
        padding-left:0;
        max-width:100%;
    }

    .discover-header{
        height:25vh;
    }

    .filter-div{
       padding:2rem;
    }

    .filter-div::after {
        content: "";
        clear: both;
        display: table;
      }

    .categorie-div{

    }

    @media only screen and (min-width:600px){
        .form-discover{
            width:45% ;
            float:left;
            margin-left: 1em;
        }
    }
    
    `}</style>

        </Container>
    )
}

export default Discover
