import React from 'react'
import {countries} from '../../datas/countriesData'

import {
  Container,
  Row,
  Col
} from 'reactstrap'


const Card = ({lesdatas}) => {



return (

  <Container style={{padding: 0,maxWidth:'100%' , margin: 0}}>

    <header className="product-header">

      
    </header>

    <div className="ap" >
Scroll Up and Down this page to see the parallax scrolling effect.
This div is just here to enable scrolling.
Tip: Try to remove the background-attachment property to remove the scrolling effect.
</div>
    

    <style jsx global>{`
    .ap{
      height:1000px;background-color:red;font-size:36px
    }
      .product-header{
        background-image: url('/images/lion-big.jpg');
        height:100%;

        background-attachment:fixed;
        background-position:center;
        background-repeat:no-repeat;
        background-size:cover
      }

    `}
    </style>
  </Container>
  

)

}

export async function getStaticProps({params}){

 
    const allpaths =  countries
 
    
   let  restos =[] 
   let leshotels = [];
    


     allpaths.map(pays => pays.restaurants).forEach(tabresto => tabresto.map(resto => restos.push(resto)))

     allpaths.map(pays => pays.hotels).forEach(tabhotel => tabhotel.map(hotel => leshotels.push(hotel)))
 
     const datas = restos.concat(leshotels)

     const lesdatas = datas.find(data => data.name === params.id)



   return {props: {lesdatas}}

}

export async function getStaticPaths(){
    
     const allpaths =  countries
  
     let paths = [] 
    let  restos =[] 
    let leshotels = [];
     


      allpaths.map(pays => pays.restaurants).forEach(tabresto => tabresto.map(resto => restos.push(resto)))

      allpaths.map(pays => pays.hotels).forEach(tabhotel => tabhotel.map(hotel => leshotels.push(hotel)))

      const datas = restos.concat(leshotels)

     paths = datas.map(data => ({
        params:{id: data.name}
    }))

     

     return{paths,fallback:false}
}

export default Card
