import {countries} from '../../../datas/countriesData'

export default (req,res) => {
   
   
 const datafilter = [] 
   let rep = [];
   
   countries.forEach(donnee => datafilter.push(donnee.hotels))

     datafilter.forEach(el => el.map(child => rep.push(child)))

    rep = datafilter.filter(el => el.id === parseInt(req.query.id))

    
    rep.length ?  res.status(200).json({id:req.query.id,name:'haha',datas:rep}) : res.status(404).json({name: `this details of id: ${req.query.id} does not exist`})
    
   
   
        
}
