import React from 'react'
import {countries} from '../../datas/countriesData'

import {
  Container,
  Row,
  Col
} from 'reactstrap'


const Card = ({lesdatas}) => {



return (

  <Container style={{padding: 0,maxWidth:'100%' , margin: 0}}>

    <header className="product-header">
    

    <img style={{objectFit:'cover'}} width="100%" height="100%" alt="undefined" src={`/images/${lesdatas.image}`} />
  
<h1>{lesdatas.name}</h1>
    </header>

   <section className="product-welcome-section">
    <h1>Bienvenue !</h1>

    <span>Far far away, behind the word mountains, far from the countries Vokalia and Consonantia, there live the blind texts. Separated they live in Bookmarksgrove right at the coast of the Semantics, a large language ocean.</span>

   </section>

   { lesdatas.rooms ?

    <section className="product-rooms-section">
        <div className="descriptif-div">
          <h1>Rooms & Suites</h1>
          <span>Far far away, behind the word mountains, far from the countries Vokalia and Consonantia, there live the blind texts. Separated they live in Bookmarksgrove right at the coast of the Semantics, a large language ocean.</span>
        </div>

        <Row xs={1} md={2} lg={3} className="rooms-images">
            <Col>
                <div className="rooms-image-div">

                  <img src="/images/afric.jpg" alt="undefined" />


                </div>

                  <h2>Single Room</h2>
                  <span>90$ / PER NIGHT</span>
            </Col>

            <Col>
                <div className="rooms-image-div">

                  <img src="/images/afric.jpg" alt="undefined" />


                </div>

                  <h2>family Room</h2>
                  <span>90$ / PER NIGHT</span>
            </Col>


            <Col>
                <div className="rooms-image-div">

                  <img src="/images/afric.jpg" alt="undefined" />


                </div>

                  <h2>Presidential Room</h2>
                  <span>90$ / PER NIGHT</span>
            </Col>

        </Row>

   </section>
   
   
   : <h1>no</h1>}

    
    

    <style jsx global>{`

   .product-header{
     height:45vh;
     width:100%
   }

   .product-header h1{
     position:absolute;
     top:20%;
     left:30%;
     z-index:1500;
     
     color:white;
     text-align:center;
     
    font-weight:900;
    font-size:5rem;
    letter-spacing: 0.2em;
    
   }

   .product-header::after{
     display:block;
     content:'';
     height:45vh;
     width:100%;
     position:absolute;
     top:0;
     background-color:black;
     opacity:0.5
   }

   .product-welcome-section{
     width:100%;
     
    padding:2rem;
    margin-bottom:2rem;
   

    
     
   }

   .product-welcome-section h1{
     font-weight:bold;
   }

   .product-welcome-section span{
     font-size:1.5rem;
     
   }


   .descriptif-div{
     text-align:center;

     
   }
   .descriptif-div h1{
    font-weight:bold
   }

   .rooms-images{
     padding-top:2rem;

   }

   .rooms-images h2,span{
     font-weight:bold;
     text-align:center
   }

   .rooms-image-div{
     height: 500px; 
     width:100%;

   }

   .rooms-image-div img{
      object-fit:cover;
      max-width:100%;
      height:100%;
   }

   

    `}
    </style>
  </Container>
  

)

}

export async function getStaticProps({params}){

 
    const allpaths =  countries
 
    
   let  restos =[] 
   let leshotels = [];
    


     allpaths.map(pays => pays.restaurants).forEach(tabresto => tabresto.map(resto => restos.push(resto)))

     allpaths.map(pays => pays.hotels).forEach(tabhotel => tabhotel.map(hotel => leshotels.push(hotel)))
 
     const datas = restos.concat(leshotels)

     const lesdatas = datas.find(data => data.name === params.id)



   return {props: {lesdatas}}

}

export async function getStaticPaths(){
    
     const allpaths =  countries
  
     let paths = [] 
    let  restos =[] 
    let leshotels = [];
     


      allpaths.map(pays => pays.restaurants).forEach(tabresto => tabresto.map(resto => restos.push(resto)))

      allpaths.map(pays => pays.hotels).forEach(tabhotel => tabhotel.map(hotel => leshotels.push(hotel)))

      const datas = restos.concat(leshotels)

     paths = datas.map(data => ({
        params:{id: data.name}
    }))

     

     return{paths,fallback:false}
}

export default Card
