import {countries} from '../../../datas/countriesData'

export default (req,res) => {
   

    const filtered = countries.filter(() => countries.map(obj => obj.hotels).find(h => h.id === req.query.id))


    if(filtered.length > 0 ){
        res.status(200).json(filtered[0])
    }
    else{
        res.status(404).json({message: `this details of id: ${id} does not exist`})
