import React from 'react'
import {UserAgent} from '@quentin-sommer/react-useragent'

export default function index() {


    return (
        <>
     <div className="index-page">
       <div className="index-header">
          <div className="presentation">
             <h3 className="salutation"></h3>
             <h1 className="africa">Africa</h1>
             <button>
               countries
             </button>
          </div>
       </div>

     </div>

     <style jsx>{`


      .presentation{
        display:block;
        text-align:center;
        position:absolute;
        top:50%;
        left:50%;
        transform:translate(-50%,-50%);
        color:white;
        z-index:1500;
        font-size:6vw;
       
      }

      .presentation h3{
        padding:0;
        margin:0;
        color:#fff;
        text-shadow: 0 2px 0 rgba(0,0,0,0.5);
        
      }

      .salutation:after{
        content:'';
        animation: animate infinite 15s
      }

      @keyframes animate{
        0%{
          content: 'Bienvenue'
        }

        20%{
          content:'Akwaaba'
        }

        40%{
          content:'Woezon'
        }

        60%{
          content:'Dalal ak diam'
        }

        80%{
          content:'Yambi'
        }

        100%{
          content:'Adanse'
        }
      }

      
     
  
     `}

     </style>
       </>
    )
}
