import React from 'react'
import {UserAgent} from '@quentin-sommer/react-useragent'

export default function index() {


    return (
        <>
     <div className="index-page">
       <div className="index-header">
          <div className="presentation">
             <h3 className="salutation"></h3>
             <h1 className="africa">Africa</h1>
             <h5 className="site-descriptif">Hotels & restaurants</h5>
             <button className="explore-button">
               Explorez
             </button>
          </div>
       </div>

     </div>

     <style jsx>{`


      .presentation{
        display:block;
        text-align:center;
        position:absolute;
        top:50%;
        left:50%;
        transform:translate(-50%,-50%);
        color:white;
        z-index:1500;
        font-size:6vw;
       
      }

      .presentation h3{
        padding:0;
        margin:0;
        color:#fff;
        text-shadow: 0 2px 0 rgba(0,0,0,0.5);
        font-weight:100;
      }

      .salutation:after{
        content:'';
        animation: animate infinite 15s
      }

      @keyframes animate{
        0%{
          content: 'Bienvenue'
        }

        20%{
          content:'Akwaaba'
        }

        40%{
          content:'Woezon'
        }

        60%{
          content:'Dalal ak diam'
        }

        80%{
          content:'Yambi'
        }

        100%{
          content:'Adanse'
        }
      }


      .site-descriptif{
        font-weight:200;
        
      }


      .explore-button{
       
        padding: 2% 10% 2% 10% ;
        margin-top: 2vh ; 
        background-color: #020202 ;
        color : #fefcfc ;
        border-radius: 5%;
       font-weight: 700;
       letter-spacing: 0.1em;
       font-size: clamp(16px,3vw,32px);
      }

      
      @media screen and (max-width:600px) , (max-device-width:600px){
        .index-header{
         
          display:block;
          width:100%;
          height:80vh;
    
          background-image:url('../images/afric.jpg') ; 
          
          background-size:cover;
          background-position:center;
          position:relative
    
        }
    
        .index-header::after{
         content:"";
         top:0;
         left:0;
         right:0;
         bottom:0;
         position:absolute;
        
         background-color:black;
         opacity:0.8
        }
        
      }
  
     `}

     </style>
       </>
    )
}
