     Button
} from 'reactstrap'



export default React.forwardRef( ({onClick,href,image, body , name},ref)=>{

 

    console.log(image)

    return(
        <a href={href} onClick={onClick} ref={ref}>
        <Card style={{height:'500px'}} className="card-container">
            <CardImg  className="image-card" height="70%"  top  src={`../images/${image}`}
            />

    

    <CardBody>
         <CardTitle>{name}</CardTitle>
         <CardText>
             {body}
             Lorem ipsum dolor sit amet, consectetur adipiscing elit. Morbi at vulputate est. Donec tempor felis at nibh eleifend malesuada. Nullam suscipit lobortis aliquam. Phasellus lobortis ante lorem, vitae scelerisque lacus tempus sed. Phasellus rutrum magna
         </CardText>
    </CardBody>
        </Card>
        

        <style jsx>{`
        
        a{
            text-decoration:none;
        color:#000;
        
        }

       

        .image-card{
           
        }
        
        `}

        </style>
        </a>
    )


})
