import React from 'react'
import {UserAgent} from '@quentin-sommer/react-useragent'

export default function index() {


    return (
        <>
     <div className="index-page">
       <div className="index-header">
          <div className="presentation">
             <h3 className="salutation"></h3>
             <h1 className="africa">Africa</h1>
             <h5 className="site-descriptif">Hotels & restaurants</h5>
            <a className="explore-link">Explorez</a>
          </div>
       </div>

     </div>

     <style jsx>{`


      .presentation{
        display:block;
        text-align:center;
        position:absolute;
        top:50%;
        left:50%;
        transform:translate(-50%,-50%);
        color:white;
        z-index:1500;
       
       
      }

      .presentation h3{
        padding:0;
        margin:0;
        color:#fff;
        text-shadow: 0 2px 0 rgba(0,0,0,0.5);
        font-weight:100;
       font-size:3rem
      }

      .salutation:after{
        content:'';
        animation: animate infinite 15s
      }

      @keyframes animate{
        0%{
          content: 'Bienvenue'
        }

        20%{
          content:'Akwaaba'
        }

        40%{
          content:'Woezon'
        }

        60%{
          content:'Dalal ak diam'
        }

        80%{
          content:'Yambi'
        }

        100%{
          content:'Adanse'
        }
      }


      .site-descriptif{
        font-weight:200;
        font-size:2rem;
      }


      .explore-link{
       display:inline-block;
        padding: 2% 10% 2% 1% ;
        margin-top: 3vh ; 
        background-color: #020202 ;
        color : #fefcfc ;
        border-radius: 5%;
       font-weight: 700;
       letter-spacing: 0.1em;
     
      }

      .index-header{
         
        display:block;
        width:100%;
        height:80vh;
  
        background-image:url('../images/afric.jpg') ; 
        
        background-size:cover;
        background-position:center;
        position:relative
  
      }
  
      .index-header::after{
       content:"";
       top:0;
       left:0;
       right:0;
       bottom:0;
       position:absolute;
      
       background-color:black;
       opacity:0.8
      }

      
      @media screen and (max-width:600px) , (max-device-width:600px){
        
        
      }
  
     `}

     </style>
       </>
    )
}
