import { countries } from '../../../datas/countriesData';
export default (({
  query: id
}, res) => {
  const filtered = countries.filter(c => c.id === id);

  if (filtered.length > 0) {
    res.status(200).json(filtered[0]);
  } else {
    res.status(404).json({
      message: `this details of id: ${id} does not exist`
    });
  }
});
