import React from 'react'
import {UserAgent} from '@quentin-sommer/react-useragent'

export default function index() {


    return (
        <>
     <div className="index-page">
       <div className="index-header">
          <div className="presentation">
             <h3 className="allOf"></h3>
             <h1 className="africa" >Africa</h1>
            
            <a className="explore-link">Explorez</a>
          </div>
       </div>

      <div className="main-business">

        <div className="services">

         
          <div className="row">

              <div>
                  <img className="services-image" width="100%" height="auto" src={require('../public/images/icons/hotel.svg')} 
                    alt="undefined"
                    title="hotelerie"
                  />

                  <h5 className="services-description">Hotelerie</h5>
              </div>

              <div>
                  <img className="services-image"  src={require('../public/images/icons/hotel.svg')} 
                    alt="undefined"
                    title="hotelerie"
                  />

                  <h5 className="services-description">Hotelerie</h5>
              </div>

        
          </div>
          
          <div className="row">
          <div>3</div>
          <div>4</div>
          </div>
          
        </div>


        <div className="culture-div">
          <div className="div-title">
              <h1>Découvrez notre culture</h1>
          </div>

          <div className="culture-text-image-div">
            <div className="culture-image-div">

            </div>

            <div className="culture-text-div">
              <h3 className="card-header">En Afrique,</h3>

              <h4 className="card-text">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Morbi at vulputate est. Donec tempor felis at nibh eleifend malesuada. Nullam suscipit lobortis aliquam. Phasellus lobortis ante lorem, vitae scelerisque lacus tempus sed. Phasellus rutrum magna</h4>
            </div>
          </div>
        </div>

      </div>

     </div>

     <style jsx>{`


      .presentation{
        display:block;
        text-align:center;
        position:absolute;
        top:50%;
        left:50%;
        transform:translate(-50%,-50%);
        color:white;
        z-index:1500;
       
       
      }

      .presentation h3{
        padding:0;
        margin:0;
       color:rgba(45,45,45,1);
        text-shadow: 0 2px 0 rgba(0,0,0,0.5);
        
       font-size:3rem
      }

      .allOf:after{
        content:'All of';
       color:rgba(45,45,45,1);
       font-size: 4rem;
       font-weight:900;
      }

      


      .explore-link{
       display:inline-block;
       border-style:solid;
       border-color:#fff;
        padding: 2% 10% 2% 10% ;
        margin-top: 3vh ; 
        background-color: #020202 ;
        color : #fefcfc ;
        border-radius: 5%;
       font-weight: 700;
       letter-spacing: 0.1em;
     
      }

      .index-header{
         
        display:block;
        width:100%;
        height:100vh;
  
        background-image:url('../images/afric.jpg') ; 
        
        background-size:cover;
        background-position:center;
        position:relative
  
      }
  
      .index-header::after{
       content:"";
       top:0;
       left:0;
       right:0;
       bottom:0;
       position:absolute;
      
       background-color:black;
       opacity:0.3
      }

      .main-business{
        display:block;
        padding:2%;

      }

     

      
    

      
      @media screen and (max-width:600px) , (max-device-width:600px){
        
        
      }
  
     `}

     </style>
       </>
    )
}
